INSERT INTO articles (id, title, content, view, creator, created_at, updated_at) VALUES (1, 'Mẹ bầu có nhóm máu Rh âm', 'Em mang thai lần đầu, vừa qua đi xét nghiệm, bác sĩ cho biết em có nhóm máu Rh âm (thai em được 27 tuần). Nghe nói, nhóm máu Rh âm sẽ nguy hiểm cho thai. Xin tư vấn em nên làm gì để sinh con an toàn?', 0,'Thúy Nga', NOW(), NOW());
INSERT INTO articles (id, title, content, view, creator, created_at, updated_at) VALUES (2, 'Viêm phế quản thể hen chữa thế nào để cải thiện tình trạng bệnh?', 'Con tôi lên 5 tuổi, cháu rất hay bị viêm phế quản (năm nào cháu cũng bị vài lần). Mỗi lần bị là cháu ho khó thở, khò khè. Theo bác sĩ nói cháu bị viêm phế quản thể hen. Xin hỏi nguyên nhân do đâu? Làm sao chữa dứt bệnh?', 0,'Quỳnh Ngân', NOW(), NOW());
INSERT INTO articles (id, title, content, view, creator, created_at, updated_at) VALUES (3, 'Loại thuốc nào cần phải thận trọng với người tăng huyết áp?', 'Tôi bị tăng huyết áp và đang phải dùng thuốc theo đơn của bác sĩ. Vừa rồi tôi bị cảm có dùng mấy viên cảm cúm. Khi uống thuốc này vào tôi thấy có triệu chứng đau đầu, hoa mắt… đo huyết áp thấy tăng cao, mặc dù tôi vẫn uống thuốc huyết áp đều đặn. Vậy có phải thuốc cảm cúm ảnh hưởng tới huyết áp của tôi không? Người tăng huyết áp cần chú ý với những loại thuốc nào, xin bác sĩ chỉ rõ.', 0,'Hoàng Lư', NOW(), NOW());
INSERT INTO articles (id, title, content, view, creator, created_at, updated_at) VALUES (4, 'Thuốc bôi da cho trẻ', 'Tôi có cháu nhỏ 4 tháng tuổi. Mấy ngày nay tự nhiên xuất hiện những mụn nhỏ li ti thành từng đám ở hai bên má rồi lan đến cằm và trán. Chúng nhanh chóng vỡ ra khiến cho da trở nên đỏ và bị rớm dịch. Có phải cháu bị chàm không? Có người mách tôi bôi nước trầu không cho chóng khỏi... Vì có con lần đầu nên tôi rất lo lắng không biết cháu bị bệnh gì và phải dùng thuốc như thế nào?', 0,'Nguyễn Thu Thuỷ', NOW(), NOW());
INSERT INTO articles (id, title, content, view, creator, created_at, updated_at) VALUES (5, 'Đau khớp, rối loạn nhịp tim là dấu hiệu bệnh gì?', 'Mẹ con năm nay 49 tuổi. Đi khám sức khỏe được chẩn đoán viêm đa khớp không xác định. Đã dùng thuốc nhưng không đỡ. Gần đây, mẹ con ngủ dậy thì thấy đau nhức người, nhất là ở vai gáy trái. Khám và đo điện tim thì bác sĩ nói là rối loạn nhịp. Mẹ con cũng bị viêm dạ dày . Xin hỏi viêm đa khớp không xác định là gì? Vấn đề tim mạch của mẹ con có sao không? Có gặp biến chứng gì nguy hiểm không?', 0,'Phạm Giàu', NOW(), NOW());
INSERT INTO articles (id, title, content, view, creator, created_at, updated_at) VALUES (6, 'Trẻ bị táo bón làm thế nào để khắc phục tình trạng này?', 'Trẻ bị táo bón làm thế nào ? Con tôi được hơn một tháng tuổi, nhưng gần đây cứ 2 - 3 ngày cháu mới đi đại tiện, tôi thường phải thụt. Tuy nhiên, cháu đi đại tiện phân vẫn bình thường, không bị táo. Mong mọi người tư vấn cho biết, cháu có vấn đề gì về tiêu hóa không? Có cách nào để bé đi vệ sinh đều không?', 0,'Lan Anh', NOW(), NOW());
INSERT INTO articles (id, title, content, view, creator, created_at, updated_at) VALUES (7, 'Nhiễm vi-rút viêm gan siêu vi B có đáng lo hay không?', 'Em đi xét nghiệm máu có HbsAg(+) còn lại bình thường, không có biểu hiện gì. Xin bác sĩ tư vấn cho em biết về căn bệnh này? Chi phí có cao không? Vì em mới chỉ đi xét nghiệm thôi. Cảm ơn bác sĩ.', 0,'giangnguyen@gmail.com', NOW(), NOW());
INSERT INTO articles (id, title, content, view, creator, created_at, updated_at) VALUES (8, 'Bị rạn da tuổi dậy thì có nguy hiểm không và xử lý thế nào?', 'Bị rạn da tuổi dậy thì có sao không? Cháu nhà mình 17 tuổi, da vùng bụng, ngực và đùi cháu có rất nhiều vết rạn, cháu rất lo lắng. Mong mòi người tư vấn chứng rạn da tuổi dậy thì có nguy hiểm không và xử lý thế nào?', 0,'Nguyễn Thị Ngòi', NOW(), NOW());
INSERT INTO articles (id, title, content, view, creator, created_at, updated_at) VALUES (9, 'Bị rối loạn tiền đình phải làm sao, ứng phó như thế nào?', 'Bị rối loạn tiền đình phải làm sao? Mấy tháng gần đây mình thường có triệu chứng hoa mắt, đi đứng mất thăng bằng, mình có đi khám, bác sĩ nói bị rối loạn tiền đình và kê đơn thuốc, mình đã uống nhưng vẫn không khỏi. Gần đây mình nằm ngủ còn hay bị bóng đè... Xin tư vấn cho mình phương pháp chữa trị.', 0,'Linh Đan', NOW(), NOW());
INSERT INTO articles (id, title, content, view, creator, created_at, updated_at) VALUES (10, 'Huyết áp kẹt có nguy hiểm không?', 'Huyết áp kẹt có nguy hiểm không? Tôi bị tăng huyết áp, hàng ngày vẫn uống thuốc đều đặn, đúng giờ theo chỉ định của bác sĩ. Mấy hôm nay tôi thấy người mệt, đau đầu, đo huyết áp thấy kẹt 115/100mmHg. Vậy không biết nguyên nhân khiến huyết áp kẹt là do đâu và bệnh có nguy hiểm không?', 0,'vancan@gmail.com', NOW(), NOW());
INSERT INTO articles (id, title, content, view, creator, created_at, updated_at) VALUES (11, 'Sưng bìu tinh hoàn ở trẻ nhỏ là bệnh gì, có nguy hiểm không?', 'Sưng bìu tinh hoàn ở trẻ nhỏ là bệnh gì? Năm nay con trai của tôi được 3 tuổi, từ khi sinh ra đến nay bìu của cháu vẫn bị sưng to, nhưng cháu không tỏ ra bị đau hay khó chịu, nên tôi nghĩ là bình thường không đưa con đi khám. Bác sĩ có thể cho biết, con tôi bị làm sao? Liệu có ảnh hưởng đến sinh sản sau này?', 0,'0905219131', NOW(), NOW());