INSERT INTO articles (id, title, content, view, creator, created_at, updated_at) VALUES (1, 'Mẹ bầu có nhóm máu Rh âm', 'Em mang thai lần đầu, vừa qua đi xét nghiệm, bác sĩ cho biết em có nhóm máu Rh âm (thai em được 27 tuần). Nghe nói, nhóm máu Rh âm sẽ nguy hiểm cho thai. Xin tư vấn em nên làm gì để sinh con an toàn?', 0,'Nga', NOW(), NOW());
INSERT INTO articles (id, title, content, view, creator, created_at, updated_at) VALUES (2, 'Viêm phế quản thể hen chữa thế nào để cải thiện tình trạng bệnh?', 'Con tôi lên 5 tuổi, cháu rất hay bị viêm phế quản (năm nào cháu cũng bị vài lần). Mỗi lần bị là cháu ho khó thở, khò khè. Theo bác sĩ nói cháu bị viêm phế quản thể hen. Xin hỏi nguyên nhân do đâu? Làm sao chữa dứt bệnh?', 0,'Ngân', NOW(), NOW());
INSERT INTO articles (id, title, content, view, creator, created_at, updated_at) VALUES (3, 'Loại thuốc nào cần phải thận trọng với người tăng huyết áp?', 'Tôi bị tăng huyết áp và đang phải dùng thuốc theo đơn của bác sĩ. Vừa rồi tôi bị cảm có dùng mấy viên cảm cúm. Khi uống thuốc này vào tôi thấy có triệu chứng đau đầu, hoa mắt… đo huyết áp thấy tăng cao, mặc dù tôi vẫn uống thuốc huyết áp đều đặn. Vậy có phải thuốc cảm cúm ảnh hưởng tới huyết áp của tôi không? Người tăng huyết áp cần chú ý với những loại thuốc nào, xin bác sĩ chỉ rõ.', 0,'Hoàng Lư', NOW(), NOW());
INSERT INTO articles (id, title, content, view, creator, created_at, updated_at) VALUES (4, 'Thuốc bôi da cho trẻ', 'Tôi có cháu nhỏ 4 tháng tuổi. Mấy ngày nay tự nhiên xuất hiện những mụn nhỏ li ti thành từng đám ở hai bên má rồi lan đến cằm và trán. Chúng nhanh chóng vỡ ra khiến cho da trở nên đỏ và bị rớm dịch. Có phải cháu bị chàm không? Có người mách tôi bôi nước trầu không cho chóng khỏi... Vì có con lần đầu nên tôi rất lo lắng không biết cháu bị bệnh gì và phải dùng thuốc như thế nào?', 0,'Nguyễn Thu Thuỷ', NOW(), NOW());
INSERT INTO articles (id, title, content, view, creator, created_at, updated_at) VALUES (5, 'Đau khớp, rối loạn nhịp tim là dấu hiệu bệnh gì?', 'Mẹ con năm nay 49 tuổi. Đi khám sức khỏe được chẩn đoán viêm đa khớp không xác định. Đã dùng thuốc nhưng không đỡ. Gần đây, mẹ con ngủ dậy thì thấy đau nhức người, nhất là ở vai gáy trái. Khám và đo điện tim thì bác sĩ nói là rối loạn nhịp. Mẹ con cũng bị viêm dạ dày . Xin hỏi viêm đa khớp không xác định là gì? Vấn đề tim mạch của mẹ con có sao không? Có gặp biến chứng gì nguy hiểm không?', 0,'Phạm Giàu', NOW(), NOW());SELECT * FROM ohc.comments;
